$(document).ready(function(){interval=window.setInterval("dyn_get(false, true)",20000);open()});var page=1;$("#input_text").NobleCount("#counter",{on_negative:function(a,b,d,c){b.css("color","red")},on_positive:function(a,b,d,c){b.css("color","green")},max_chars:250,on_update:function(a,b,d,c){if(c<=10){b.css("color","red")}}});function dyn_get(b,d,a,c){if(d){$.get("api.php?dyn_get=1&latest="+b+"&page="+page,function(e){$("#twibber").html(e)});return false}if(a!=""){$.get("api.php?latest="+b+"&page="+page,{nick:a},function(e){$("#twibber").html(e)});return false}if(c!=""){$.get("api.php?latest="+b+"&page="+page,{search:c},function(e){$("#twibber").html(e)});return false}return true}function dyn_submit(){var a=$("#input_text").val();if(a.replace(/^\s+|\s+$/g,"")!=""&&a.length<=250&&$("#nickname").val()!=""){$.post("api.php?new_entry=1",{text:a},function(b){dyn_get(true,true);$("#status").freeow(b,b.replace(/!/,"")+" gesendet!",{classes:["smokey"],autoHideDelay:2500})});$("#input_text").val("");$("#counter").text("250")}else{alert("Error! Nachricht zu lang, keine Nachricht vorhanden oder kein Nickname eingegeben.")}}function insert_nick(a){$("#input_text").focus();$("#input_text").val("@"+a+" "+$("#input_text").val())}function open(){$("#open").click(function(){$("#panel").slideDown("slow").delay(1).css("display","block")});$("#close").click(function(){$("#panel").slideUp("slow")});$("#toggle a").click(function(){$("#toggle a").toggle()})}function load_dips(c,a,b){page++;$("#status").freeow("Loading","Neue Twibbs werden geladen!",{classes:["smokey"],autoHideDelay:2500});dyn_get(false,c,a,b)};