$(document).ready(function(){$("#input_text").keyup(function(){count_char()});interval=window.setInterval("dyn_get(true, true)",20000)});function count_char(){var a=$("#input_text").val();$("label[for='input_text']").text(a.length+" Zeichen")}function dyn_get(b,d,a,c){if(d){$.get("api.php?dyn_get=1",function(e){$("#twibber").html(e)});return false}if(a!=""){$.get("api.php",{nick:a},function(e){$("#twibber").html(e)});return false}if(c!=""){$.get("api.php",{search:c},function(e){$("#twibber").html(e)});return false}return true}function dyn_submit(){var a=$("#input_text").val();if(a.replace(/^\s+|\s+$/g,"")!=""&&a.length<=250&&$("#nickname").val()!=""){$.post("api.php?new_entry=1",{text:a},function(b){$("#status").fadeIn().text(b);dyn_get(true,true)});$("#input_text").val("")}else{alert("Error! Nachricht zu lang, keine Nachricht vorhanden oder kein Nickname eingegeben.")}}function insert_nick(a){$("#input_text").val("@"+a+" "+$("#input_text").val());$("#input_text").focus()};